return items.map(item => {
  const data = item.json;

  const pronoun = data.male ? 'He' : 'She';
  const interests = data.interests.join(', ').replace(/, ([^,]*)$/, ' and $1');

  const bio = `${data.name.charAt(0).toUpperCase() + data.name.slice(1)} is ${data.age} years old. ${pronoun} loves ${interests}.`;

  return {
    json: {
      ...data,
      bio
    }
  };
});

