// n8n Code Node - Combine transcript text into one string (no newlines)

// Get the input data (first item from the input)
const inputData = $input.all()[0].json;

// Extract the data array
const transcriptData = inputData.data;

// Filter out items that don't have text and combine all text segments
const combinedText = transcriptData
  .filter(item => item.text) // Only include items with text property
  .map(item => item.text.trim().replace(/\n/g, ' ')) // Get the text, trim whitespace, and replace newlines with spaces
  .join(' ') // Join with spaces
  .replace(/\s+/g, ' '); // Replace multiple spaces with single space

// Return the combined text as a single item
return [
  {
    json: {
      combinedText: combinedText,
      originalItemCount: transcriptData.length,
      textSegmentCount: transcriptData.filter(item => item.text).length
    }
  }
];

// n8n Code Node - Group transcript into chunks of 20 with timestamps

// Get the input data (first item from the input)
const inputData = $input.all()[0].json;

// Extract the data array and filter out items without text
const transcriptData = inputData.data.filter(item => item.text);

// Group into chunks of 20
const chunkSize = 20;
const chunks = [];

for (let i = 0; i < transcriptData.length; i += chunkSize) {
  const chunk = transcriptData.slice(i, i + chunkSize);

  // Combine all text from this chunk
  const combinedText = chunk
    .map(item => item.text.trim())
    .join(' ');

  // Calculate timestamp range
  const firstItem = chunk[0];
  const lastItem = chunk[chunk.length - 1];

  const startTime = parseFloat(firstItem.start);
  const endTime = parseFloat(lastItem.start) + parseFloat(lastItem.dur);

  // Helper function to format seconds to MM:SS format
  const formatTime = (seconds) => {
    if (seconds < 60) {
      return `${seconds.toFixed(3)}s`;
    }
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toFixed(3).padStart(6, '0')}`;
  };

  // Create the grouped object
  chunks.push({
    json: {
      text: combinedText,
      timestamp: {
        start: startTime,
        end: endTime,
        duration: endTime - startTime,
        startFormatted: formatTime(startTime),
        endFormatted: formatTime(endTime),
        durationFormatted: formatTime(endTime - startTime)
      },
      itemCount: chunk.length,
      chunkNumber: Math.floor(i / chunkSize) + 1
    }
  });
}

// Return all chunks as separate items
return chunks;
